mapper.select:
SELECT /*+ INDEX(A1 PK_TBLIV_BILL) */ 'N' AS GUBUN , A1.BILL_SEQ_NO , A1.USE_YYMM , A1.PROD_CMPS_ID , A1.SVC_CMPS_ID , A1.CHRG_ITM_CD , A1.SO_ID , A1.CUST_ID , A1.GRP_ID , A1.CTRT_ID , A1.BILL_CYCL , A1.ADJ_PRV_BILL_AMT AS ADJ_PRV_BILL_AMT /* 청구전조정금액 */ , A1.ADJ_AMT AS ADJ_AMT /* 조정금액 */ , A1.BILL_AMT AS ADJ_AFT_BILL_AMT /* 청구금액 */ , IFNULL(C1.ADJ_APPL_AMT,0) AS ADJ_APPL_AMT /* 신청금액 */ , IFNULL(C1.ADJ_APPL_AMT,0) AS ORG_ADJ_APPL_AMT , A1.RCPT_AMT /* 수납금액 */ , A1.BILL_AMT - RCPT_AMT AS UNPAY_AMT /* 미납금액 */ , C1.ADJ_NO , C1.ADJ_RESN_CD , IFNULL(C1.ADJ_PT, '2') AS ADJ_PT /* 조정시점(1:청구전,2:청구후,3:일회성) */ , A1.CRNCY_CD , A1.EXRATE , A1.EXRATE_APLY_DT , A1.PROD_CD , A1.SVC_CD , IFNULL(D1.ATTR_VAL, 'X') AS ATTR_VAL /* 요금조정대상항목여부(1:대상, X:미대상) */ , A1.BILL_MM_TP /* 청구대상월구분(0:당납, 1:후납) */ , A1.USG_CNT , (CASE #DECODE(C1.ADJ_NO, NULL, TO_CHAR(SYSDATE,'YYYYMM'), C1.HOPE_APLY_YYMM) #WHEN TO_CHAR(SYSDATE,'YYYYMM') THEN '0' CASE C1.ADJ_NO WHEN NULL THEN DATE_FORMAT(NOW(),'%Y%m') ELSE C1.HOPE_APLY_YYMM END WHEN DATE_FORMAT(NOW(),'%Y%m') THEN '0' ELSE '1' END) AS MM_TP /* 요금조정대상항목여부(0:당납상품만대상, 1:당후납모든상품)*/ , E1.CUST_NM AS CUST_NM , F1.PROD_NM AS PROD_NM , D2.SVC_RATE_ITM_TYP_NM AS CHRG_ITM_NM , (SELECT SO_NM FROM TSYCO_SO_MAST X /* SO 마스타*/ WHERE X.SO_ID = A1.SO_ID ) AS SO_NM , E2.SVC_TEL_NO AS SVC_TEL_NO FROM TBLIV_BILL A1 /* 청구내역 */ LEFT OUTER JOIN (SELECT B1.BILL_SEQ_NO , B2.ADJ_NO , B2.USE_YYMM , B2.PROD_CMPS_ID , B2.SVC_CMPS_ID , B2.CHRG_ITM_CD , B2.ADJ_PRV_BILL_AMT , B2.ADJ_AMT , B2.ADJ_AFT_BILL_AMT , B2.ADJ_APPL_AMT , B1.ADJ_PT , B1.HOPE_APLY_YYMM , B1.ADJ_RESN_CD FROM TBLIV_CHRG_ADJ_APLY B1 /* 요금조정신청내역 */ , TBLIV_CHRG_ADJ_APLY_DTL B2 /* 요금조정신청상세내역 */ WHERE B1.ADJ_NO = B2.ADJ_NO
AND B1.ADJ_NO = '0000000000'
AND B1.ADJ_NO = #{billingAdjust.adjNo}
AND B1.ADJ_PT = #{billingAdjust.adjPt} ) C1 ON A1.BILL_SEQ_NO = C1.BILL_SEQ_NO AND A1.USE_YYMM = C1.USE_YYMM AND A1.PROD_CMPS_ID = C1.PROD_CMPS_ID AND A1.SVC_CMPS_ID = C1.SVC_CMPS_ID AND A1.CHRG_ITM_CD = C1.CHRG_ITM_CD , IFNBRM_SVC_RATE_ITM_ATTR_INFO D1 /* 서비스과금항목유형속성 */ , IFNBRM_SVC_RATE_ITM_INFO D2 /* 서비스과금항목유형 */ , IFNBRM_CUST_INFO E1 , IFNBRM_CTRT_INFO E2 , IFNBRM_PROD_INFO F1 WHERE 1=1 AND A1.BILL_SEQ_NO = #{billingAdjust.billSeqNo} AND A1.SO_ID = #{billingAdjust.soId} AND A1.SO_ID = D1.SO_ID AND A1.SO_ID = D2.SO_ID AND A1.SO_ID = E1.SO_ID AND A1.SO_ID = E2.SO_ID AND A1.SO_ID = F1.SO_ID AND A1.CHRG_ITM_CD = D1.SVC_RATE_ITM_TYP_CD AND A1.CHRG_ITM_CD = D2.SVC_RATE_ITM_TYP_CD AND A1.CTRT_ID = E2.CTRT_ID AND A1.CUST_ID = E1.CUST_ID AND A1.PYM_ACNT_ID = E1.PYM_ACNT_ID AND D1.INACT_DT = '99991231' AND E2.INACT_DTTM = '99991231235959' AND A1.SVC_CMPS_ID = E2.SVC_CMPS_ID AND A1.PROD_CMPS_ID = E2.PROD_CMPS_ID AND A1.PROD_CD = F1.PROD_CD
AND A1.BILL_YYMM = #{billingAdjust.billYymm}
AND D1.ATTR_CD = 'AT006' /* 속성코드(요금조정대상항목) Vads에서는 AT006 코드로 변경해야함*/ AND D1.ATTR_VAL = '1' /* 속성값 (요금조정 대상) */ ORDER BY SO_NM, CUST_NM, A1.CTRT_ID, C1.USE_YYMM, PROD_NM, SVC_NM, D2.DISP_PRI_NO
