mapper.select:
SELECT COUNT(*)
FROM TBLIV_BAT_GRP
WHERE BAT_GRP_ID = #{batGrpId}

mapper.insert:
INSERT INTO TBLIV_BAT_GRP
(
	BAT_GRP_ID
	, BAT_GRP_NM
	, BAT_GRP_TP
	, BAT_PROC_TP
	, REGR_ID
	, REG_DATE
	, CHG_DATE
	, CHGR_ID
)
VALUES
(
	#{batGrpId}
	, #{batGrpNm}
	, #{batGrpTp}
	, #{batProcTp}
	, #{regrId}
	, #{regDate}
	, #{chgDate}
	, #{chgrId}
)

mapper.update:
UPDATE TBLIV_BAT_GRP
BAT_GRP_NM = #{batGrpNm},
BAT_GRP_TP = #{batGrpTp},
BAT_PROC_TP = #{batProcTp},
CHGR_ID = #{chgrId}, CHG_DATE = #{chgDate}
