mapper.select:
SELECT COUNT(*)
FROM TBLIV_BAT_PGM
WHERE BAT_PGM_ID = #{batPgmId}

mapper.insert:
INSERT INTO TBLIV_BAT_PGM
(
	BAT_PGM_ID
	, PGM_NM
	, EXEC_OBJ
	, PKG_NM
	, CLS_STP_CL
	, CLS_TSK_CL
	, PGM_CT
	, REGR_ID
	, REG_DATE
	, CHGR_ID
	, CHG_DATE
)
VALUES
(
	#{batPgmId}
	, #{pgmNm}
	, #{execObj}
	, #{pkgNm}
	, #{clsStpCl}
	, #{clsTskCl}
	, #{pgmCt}
	, #{regrId}
	, #{regDate}
	, #{chgrId}
	, #{chgDate}
)

where.if:
BAT_PGM_ID NOT IN
(
	SELECT BAT_PGM_ID
	FROM TBLIV_BAT_WRK_MAP
	WHERE BAT_GRP_ID = #{batGrpId}
)
