mapper.select:
SELECT DPST_AMT
FROM TBLPY_DPST
WHERE DPST_SEQ_NO = #{dpstSeqNo}

mapper.update:
UPDATE TBLPY_DPST SET
	PAY_PROC_YN = 'Y'
	, PAY_PROC_DT = #{payProcDt}
WHERE DPST_SEQ_NO = #{dpstSeqNo}

