mapper.select:
SELECT C.CNCL_DT AS CNCL_DT /* 수납취소일시 */ , E.USER_NAME AS CNCL_EMP_NM /* 취소자명 */ , A.PYM_ACNT_ID AS PYM_ACNT_ID /* 납부계정ID */ ,(SELECT DISTINCT BB.ACNT_NM FROM IFNBRM_CUST_INFO BB WHERE A.PYM_ACNT_ID = BB.PYM_ACNT_ID ) AS ACNT_NM /* 납부계정명 */ , A.BILL_YYMM AS BILL_YYMM /* 청구년월 */ , A.TRANS_DT AS TRANS_DT /* 이체일자 */ , A.DPST_DT AS DPST_DT /* 입금일자 */ ,(SELECT NAME.CODE_NM FROM TSYCO_CODE_MAST MAST , TSYCO_CODE_MAST_NAME MNAME , TSYCO_CODE_DETAIL DETAIL , TSYCO_CODE_DETAIL_NAME NAME WHERE MAST.COMMON_GRP = DETAIL.COMMON_GRP AND MAST.COMMON_GRP = MNAME.COMMON_GRP AND MNAME.LNG_TYP = #{receiptCancel.lngTyp} AND DETAIL.COMMON_GRP = NAME.COMMON_GRP AND DETAIL.COMMON_CD = NAME.COMMON_CD AND NAME.LNG_TYP = #{receiptCancel.lngTyp} AND MAST.COMMON_GRP = 'BL00067' AND DETAIL.COMMON_CD = A.DPST_CL AND DETAIL.USE_YN = 'Y' AND MAST.USE_YN = 'Y' ) AS DPST_CL_NM /* 입금구분명(BLPY001) */ ,(SELECT NAME.CODE_NM FROM TSYCO_CODE_MAST MAST , TSYCO_CODE_MAST_NAME MNAME , TSYCO_CODE_DETAIL DETAIL , TSYCO_CODE_DETAIL_NAME NAME WHERE MAST.COMMON_GRP = DETAIL.COMMON_GRP AND MAST.COMMON_GRP = MNAME.COMMON_GRP AND MNAME.LNG_TYP = #{receiptCancel.lngTyp} AND DETAIL.COMMON_GRP = NAME.COMMON_GRP AND DETAIL.COMMON_CD = NAME.COMMON_CD AND NAME.LNG_TYP = #{receiptCancel.lngTyp} AND MAST.COMMON_GRP = 'BL00079' AND DETAIL.COMMON_CD = A.PAY_TP AND DETAIL.USE_YN = 'Y' AND MAST.USE_YN = 'Y' ) AS PAY_TP_NM /* 수납구분명(BLPY013) */ , A.PAY_APLY_AMT AS BILL_AMT /* 청구금액 */ , A.PRE_RCPT_AMT AS PRE_RCPT_AMT /* 이전수납금액 */ , A.RCPT_AMT AS RCPT_AMT /* 수납금액 */ , A.PAY_PROC_DT AS PAY_PROC_DT /* 수납처리일자 */ , A.DPST_PROC_DT AS DPST_PROC_DT /* 입금처리일자 */ , C.CNCL_EMP_ID AS CNCL_EMP_ID /* 취소자 */ , A.PYM_SEQ_NO AS PYM_SEQ_NO /* 수납일련번호 */ , A.BILL_DT AS BILL_DT /* 청구일자 */ , A.BILL_SEQ_NO AS BILL_SEQ_NO /* 청구일련번호 */ , D.USER_NAME AS USR_NM /* 등록자명 */ , A.REGR_ID AS REGR_ID /* 등록자ID */ , A.REG_DATE AS REG_DATE /* 등록일 */ , A.DPST_CL AS DPST_CL /* 입금구분 */ , A.PAY_TP AS PAY_TP /* 수납구분 */ FROM TBLPY_RCPT A , TBLPY_RCPT_CNCL C , TSYCO_USER D , TSYCO_USER E WHERE 1 = 1 AND A.SO_ID = #{receiptCancel.soId} /* SO_ID */ AND A.PYM_SEQ_NO = C.PYM_SEQ_NO AND A.REGR_ID = D.USER_ID AND C.CNCL_EMP_ID = E.USER_ID AND A.DPST_CL <> '22' /* 필수조건 */ AND A.DPST_CL <> '40' /* 필수조건(청구차감된 원포인트는 수납취소불가) */ /* 입금일자 */ AND A.DPST_DT BETWEEN #{receiptCancel.searchStatDt} AND #{receiptCancel.searchEndDt}
/* 이체일자 */ AND A.TRANS_DT BETWEEN #{receiptCancel.searchStatDt} AND #{receiptCancel.searchEndDt}
/* 수납처리일자 */ AND A.PAY_PROC_DT BETWEEN #{receiptCancel.searchStatDt} AND #{receiptCancel.searchEndDt}
/* 취소일자 */ AND C.CNCL_DT BETWEEN #{receiptCancel.searchStatDt} AND #{receiptCancel.searchEndDt}
AND C.CNCL_EMP_ID = #{receiptCancel.searchCustId} /* 취소자ID */
AND C.PYM_ACNT_ID = #{receiptCancel.searchPymAcntId} /* 납부계정 */

SELECT COUNT(*)
FROM   TBLPY_RCPT A
     , TBLPY_RCPT_CNCL C
     , TSYCO_USER D
     , TSYCO_USER E

WHERE  1 = 1
AND    A.SO_ID            = #{receiptCancel.soId}                                    /* SO_ID */
AND    A.PYM_SEQ_NO       = C.PYM_SEQ_NO
AND    A.REGR_ID          = D.USER_ID
AND    C.CNCL_EMP_ID      = E.USER_ID
AND    A.DPST_CL  <>  '22' /* 필수조건 */
AND    A.DPST_CL  <>  '40' /* 필수조건(청구차감된 원포인트는 수납취소불가) */
  /* 입금일자 */
AND    A.DPST_DT BETWEEN #{receiptCancel.searchStatDt} AND #{receiptCancel.searchEndDt}

  /* 이체일자  */
AND    A.TRANS_DT BETWEEN #{receiptCancel.searchStatDt} AND #{receiptCancel.searchEndDt}

  /* 수납처리일자 */
AND    A.PAY_PROC_DT BETWEEN #{receiptCancel.searchStatDt} AND #{receiptCancel.searchEndDt}

  /* 취소일자 */
AND    C.CNCL_DT BETWEEN #{receiptCancel.searchStatDt} AND #{receiptCancel.searchEndDt}


AND    C.CNCL_EMP_ID      = #{receiptCancel.searchCustId}      /* 취소자ID */


AND    C.PYM_ACNT_ID      = #{receiptCancel.searchPymAcntId}       /* 납부계정 */
