mapper.select:
SELECT '0' CHK, A.FCTR_CD, A.ACT_DT, A.INACT_DT, A.FCTR_NM, A.REGR_ID, REG_DATE, A.CHGR_ID, CHG_DATE, ( SELECT C.CODE_NM AS COMMON_CD_NM FROM TSYCO_CODE_DETAIL B LEFT OUTER JOIN TSYCO_CODE_DETAIL_NAME C ON #{factor.lngTyp} = C.LNG_TYP AND B.COMMON_GRP = C.COMMON_GRP AND B.COMMON_CD = C.COMMON_CD WHERE B.COMMON_GRP = 'PP00012' AND B.COMMON_CD = FCTR_REF_TYP ) AS FCTR_REF_TYP, TABLE_ID, COLMN_ID, ( SELECT C.CODE_NM AS COMMON_CD_NM FROM TSYCO_CODE_DETAIL B LEFT OUTER JOIN TSYCO_CODE_DETAIL_NAME C ON #{factor.lngTyp} = C.LNG_TYP AND B.COMMON_GRP = C.COMMON_GRP AND B.COMMON_CD = C.COMMON_CD WHERE B.COMMON_GRP = 'PP00024' AND B.COMMON_CD = DATA_TYP ) AS DATA_TYP, REF_CD, REF_FUNC, ( CASE WHEN CHG_NULL_FL = '0' THEN 'N' WHEN CHG_NULL_FL = '1' THEN 'Y' ELSE 'N' END ) AS CHG_NULL_FL, CHG_NULL_VAL FROM TPMBI_FCTR A WHERE A.MSTR_FL = '1' AND A.INACT_DT >= #{factor.currentDay}
AND FCTR_NM LIKE CONCAT(CONCAT('%', #{factor.fctrNm}), '%')
ORDER BY A.FCTR_CD DESC

	SELECT (CASE WHEN COUNT(FCTR_CD) IS NULL
				THEN 0
				ELSE COUNT(FCTR_CD) 
				END ) AS DATA_CNT
	FROM  TPMBI_FCTR
	WHERE FCTR_NM     = #{factor.fctrNm}
	AND   MSTR_FL     = '1'
	AND   INACT_DT  > #{factor.currentDay}

       AND FCTR_CD       != #{factor.fctrCd}

SELECT (CASE WHEN COUNT(FCTR_CD) IS NULL
			THEN 0
			ELSE COUNT(FCTR_CD) 
			END ) AS DATA_CNT 
FROM  TPMBI_FCTR
WHERE FCTR_NM  	    = 	#{factor.fctrNm}
AND   MSTR_FL       = 	'1'
AND   INACT_DT    >   #{factor.currentDay}
AND   FCTR_CD      != 	#{factor.fctrCd}

mapper.update:
	    UPDATE TPMBI_FCTR
	    SET    INACT_DT          =  #{factor.inactDt, jdbcType=VARCHAR },
		       MSTR_FL           =  #{factor.mstrFl, jdbcType=VARCHAR },
               CHGR_ID           =  #{factor.chgrId, jdbcType=VARCHAR },    
               CHG_DATE          =  #{factor.sysDate}
        WHERE  FCTR_CD         	 =  #{factor.fctrCd, jdbcType=VARCHAR }
		AND    ACT_DT            =  #{factor.baseActDt, jdbcType=VARCHAR }
