	/* PaymentCancelMapper.xml.insertPrepayOcc, gwhan, 2020.03.20 */
    INSERT INTO TBLPY_PREPAY_OCC
    (
        SO_ID
      , PREPAY_OCC_SEQ_NO
      , PYM_ACNT_ID
      , PREPAY_OCC_DTTM
      , PREPAY_OCC_TP
      , PREPAY_OCC_RESN
      , PREPAY_OCC_TGT_SEQ_NO
      , DPST_DT
      , DPST_PROC_DT
      , PREPAY_PROC_STAT
      , PREPAY_OCC_AMT
      , PREPAY_TRANS_AMT
      , PREPAY_BAL
      , TRANS_CMPL_YN
      , CNCL_YN
      , CNCL_DTTM
      , TRANS_DT
      , ERP_SND_YN
      , INPT_MENU_ID
      , REGR_ID
      , REG_DATE
      , CHGR_ID
      , CHG_DATE
    )
    SELECT SO_ID
         , #{prepayOccVO.prepayOccSeqNo}
         , #{prepayOccVO.pymAcntId}
         , #{prepayOccVO.prepayOccDttm}
         , #{prepayOccVO.prepayOccTp}
         , #{prepayOccVO.prepayOccResn}
         , #{prepayOccVO.prepayOccTgtSeqNo}
         , DPST_DT
         , DPST_PROC_DT
         , #{prepayOccVO.prepayProcStat}
         , RCPT_AMT
         , 0
         , RCPT_AMT
         , 'N'
         , #{prepayOccVO.cnclYn}
         , #{prepayOccVO.cnclDttm}
         , #{prepayOccVO.transDt}
         , #{prepayOccVO.erpSndYn}
         , #{prepayOccVO.inptMenuId}
         , #{prepayOccVO.regrId}
         , #{prepayOccVO.regDate}
         , #{prepayOccVO.regrId}
         , #{prepayOccVO.regDate}
    FROM   TBLPY_RCPT
    WHERE  PYM_SEQ_NO = #{prepayOccVO.prepayOccTgtSeqNo}

