mapper.select:
SELECT A.SO_ID AS SO_ID , B.SET_ITM_ID AS SET_ITM_CD , B.EFT_BGN_YYMM AS EFT_BGN_YYMM , B.EFT_END_YYMM AS EFT_END_YYMM , B.SET_ITM_NM AS SET_ITM_NM , B.SET_LOC AS REFERENCE_TYPE , (SELECT NAME.CODE_NM AS COMMON_CD_NM FROM TSYCO_CODE_MAST MAST , TSYCO_CODE_MAST_NAME MNAME , TSYCO_CODE_DETAIL DETAIL , TSYCO_CODE_DETAIL_NAME NAME WHERE MAST.COMMON_GRP=DETAIL.COMMON_GRP AND MAST.COMMON_GRP =MNAME.COMMON_GRP AND MNAME.LNG_TYP =#{billingCycleInfoVO.lngTyp} AND DETAIL.COMMON_GRP =NAME.COMMON_GRP AND DETAIL.COMMON_CD =NAME.COMMON_CD AND NAME.LNG_TYP = #{billingCycleInfoVO.lngTyp} AND MAST.COMMON_GRP ='BL00048' AND DETAIL.COMMON_CD = B.ESS_YN ) AS REQUIRED , B.ESS_YN AS REQUIRED_CD , (SELECT NAME.CODE_NM AS COMMON_CD_NM FROM TSYCO_CODE_MAST MAST , TSYCO_CODE_MAST_NAME MNAME , TSYCO_CODE_DETAIL DETAIL , TSYCO_CODE_DETAIL_NAME NAME WHERE MAST.COMMON_GRP=DETAIL.COMMON_GRP AND MAST.COMMON_GRP =MNAME.COMMON_GRP AND MNAME.LNG_TYP =#{billingCycleInfoVO.lngTyp} AND DETAIL.COMMON_GRP =NAME.COMMON_GRP AND DETAIL.COMMON_CD =NAME.COMMON_CD AND NAME.LNG_TYP = #{billingCycleInfoVO.lngTyp} AND MAST.COMMON_GRP ='BL00034' AND DETAIL.COMMON_CD = B.FLD_TP ) AS FIELD_NATURE , B.FLD_TP AS FIELD_NATURE_CD , B.FLD_STRUCT AS FIELD_STRUCTURE , B.FLD_CNT AS FIELD_DIGIT , A.SET_VAL AS SET_VAL , CASE WHEN A.BILL_YYMM = NULL THEN #{billingCycleInfoVO.searchBillYymm} ELSE A.BILL_YYMM END AS BILL_YYMM , CASE WHEN A.BILL_CYCL = NULL THEN #{billingCycleInfoVO.billCycl} ELSE A.BILL_CYCL END AS BILL_CYCL , (SELECT SO_NM FROM TSYCO_SO_MAST WHERE SO_ID = A.SO_ID) AS SO_NM FROM TBLIV_BILL_CYCL_STP A /* 빌링주기설정정보 */ /* 빌링설정항목 */ LEFT OUTER JOIN TBLIV_BILL_STP_ITM B ON B.SET_ITM_ID = A.SET_ITM_ID AND B.EFT_BGN_YYMM = A.EFT_BGN_YYMM WHERE #{billingCycleInfoVO.searchBillYymm} BETWEEN B.EFT_BGN_YYMM AND B.EFT_END_YYMM AND A.BILL_YYMM = #{billingCycleInfoVO.searchBillYymm} AND A.BILL_CYCL = #{billingCycleInfoVO.billCycl} AND B.SET_LOC = '2'
AND A.SO_ID = #{billingCycleInfoVO.searchSoId}
ORDER BY A.SO_ID, B.SET_ITM_ID, B.EFT_BGN_YYMM

		SELECT   COUNT(0)
		   FROM TBLIV_BILL_CYCL_STP  A                     /* 빌링주기설정정보 */
              		/* 빌링설정항목 */
                   LEFT OUTER JOIN TBLIV_BILL_STP_ITM B ON B.SET_ITM_ID = A.SET_ITM_ID AND B.EFT_BGN_YYMM = A.EFT_BGN_YYMM 
             WHERE #{billingCycleInfoVO.searchBillYymm} BETWEEN B.EFT_BGN_YYMM AND B.EFT_END_YYMM
                 AND A.BILL_YYMM     = #{billingCycleInfoVO.searchBillYymm}
                 AND A.BILL_CYCL     = #{billingCycleInfoVO.billCycl}
                 AND B.SET_LOC       = '2'

			     AND A.SO_ID =  #{billingCycleInfoVO.searchSoId}

		SELECT COUNT(*)
          FROM  TBLIV_BILL_CYCL_STP              /* 빌링주기설정정보 */
        WHERE  1=1

			AND  SO_ID =  #{billingCycleInfoVO.searchSoId}


			AND  BILL_YYMM =  #{billingCycleInfoVO.lastYymm}

SELECT  COUNT(*)
	 FROM TBLIV_BILL_CYCL_STP
 WHERE SO_ID = #{billingCycleInfoVO.soId}
     AND BILL_YYMM = #{billingCycleInfoVO.billYymm}

mapper.delete:
DELETE
   FROM  TBLIV_BILL_CYCL_STP         /* 빌링주기설정정보 */
 WHERE  1=1
     AND  BILL_YYMM    = #{billingCycleInfoVO.billYymm}
     AND  BILL_CYCL     = #{billingCycleInfoVO.billCycl}
     AND  SET_ITM_ID   = #{billingCycleInfoVO.setItmCd}
     AND  SO_ID           = #{billingCycleInfoVO.soId}

			DELETE 
			   FROM TBLIV_BILL_CYCL_STP   /* 빌링주기설정정보 */
             WHERE 1=1
                 AND BILL_YYMM    = #{billingCycleInfoVO.searchBillYymm}

                 AND SO_ID           = #{billingCycleInfoVO.searchSoId}

mapper.update:
		UPDATE  TBLIV_BILL_CYCL_STP                            /* 빌링주기설정정보 */
			SET  SET_VAL	= #{billingCycleInfoVO.setVal}
				,CHGR_ID	= #{billingCycleInfoVO.chgId}
				,CHG_DATE	= #{billingCycleInfoVO.chgDate}
		WHERE  1=1
             AND  BILL_YYMM		= #{billingCycleInfoVO.billYymm}
             AND  SET_ITM_ID	= #{billingCycleInfoVO.setItmCd}
             AND  SO_ID			= #{billingCycleInfoVO.soId}
