mapper.select:
SELECT
	A.BAT_GRP_ID
	,A.BS_YYMM
	,A.BAT_PGM_ID
	,A.PGM_EXE_SEQ_NO
	,A.SO_ID
	,A.BAT_PROC_STAT
FROM 
	TBLIV_BAT_PGM_LOG A
WHERE A.BAT_GRP_ID		= #{batGrpId}
	AND A.BAT_PGM_ID	= #{batPgmId}			
	AND A.SO_ID			= #{soId}	

		AND A.PGM_EXE_SEQ_NO	= #{pgmExeSeqNo}


	AND A.BS_YYMM		= #{bsYymm}

ORDER BY A.PGM_EXE_SEQ_NO DESC

SELECT
	A.CLC_WRK_NO
FROM 
	TBLCH_CLC_MAIN A
WHERE A.BILL_YYMM		= #{billYymm}
	AND A.SO_ID			= #{soId}
	AND A.BILL_CYCL		= #{billCycl}

mapper.update:
	UPDATE TBLIV_BAT_PGM_LOG
         SET BAT_PROC_STAT 	= #{batProcStat}

	WHERE PGM_EXE_SEQ_NO	= #{pgmExeSeqNo}
		AND BAT_GRP_ID   	= #{batGrpId}
		AND BS_YYMM			= #{bsYymm}
		AND BAT_PGM_ID		= #{batPgmId}
		AND SO_ID			= #{soId}

mapper.select:
	/*com.ntels.ccbs.charge.mapper.charge.charge.ChargeCalculationMapper.getChargeListTotalCnt*/
 		SELECT
   			COUNT(*)
		FROM TBLCH_CLC_MAIN        A                           /* 요금게산메인 */
			,TBLCH_SVC_CMPS_MT     B                           /* 서비스구성Master */
			, TBLCH_CHRG           C                           /* 요금내역 */
		WHERE A.CLC_WRK_NO      = B.CLC_WRK_NO
		   AND B.CLC_WRK_NO      = C.CLC_WRK_NO
		   AND B.BILL_YYMM       = C.BILL_YYMM
		   AND B.BILL_CYCL       = C.BILL_CYCL
		   AND B.USE_YYMM        = C.USE_YYMM
		   AND B.SVC_CMPS_ID     = C.SVC_CMPS_ID
		   AND B.SO_ID           = C.SO_ID
           AND A.CLC_WRK_CL      = '5'           /* 계산작업구분(5.계산) */
		   AND B.BILL_YYMM = #{condBillYymm}     /* 청구년월 */         
	       		/* 계산처리상태 */
	       		AND A.CLC_PROC_STAT = #{condClc}

	       				/* 사업구분 */
	       		AND A.SO_ID = #{soId}

	       	/* 납부계정 */
	       		AND B.PYM_ACNT_ID = #{condPymAcntId}

	       			/* 고객 */
	       		AND B.CUST_ID  = #{condCustId}

/*com.ntels.ccbs.charge.mapper.charge.charge.ChargeCalculationMapper.getChargeList*/ SELECT RESULT.* FROM (
SELECT B.SO_ID , B.BILL_YYMM , B.BILL_CYCL , B.PYM_ACNT_ID , B.CUST_ID , B.CTRT_ID -- , FN_SETTELNO(FBLIV_GET_CTRT_MDN(B.CTRT_ID)) AS SVC_TEL_NO , B.PROD_CD , A.CLC_WRK_CL , (SELECT NVL(C.CODE_NM, B.COMMON_CD_NM) as COMMON_CD_NM FROM TSYCO_CODE_DETAIL B LEFT OUTER JOIN TSYCO_CODE_DETAIL_NAME C on C.LNG_TYP = #{lng} AND C.COMMON_GRP = B.COMMON_GRP and C.COMMON_CD = B.COMMON_CD WHERE B.COMMON_GRP = 'BLCH003' AND B.COMMON_CD = A.CLC_WRK_CL) AS CLC_WRK_CL_NM , SUM(C.USE_AMT) AS TOT_USE_AMT , SUM(C.USE_CNT) AS TOT_USE_CNT , (SELECT CUST_NM FROM TCMCU_CUST_INFO X /* 고객정보 */ WHERE X.CUST_ID = B.CUST_ID and x.so_id = b.so_id ) AS CUST_NM , (SELECT PROD_NM FROM TPMPD_PROD X /* 상품정보 */ WHERE X.PROD_CD = B.PROD_CD AND X.MSTR_FL = '1' ) AS PROD_NM , (SELECT SO_NM FROM TSYCO_SO_MAST X /* SO 마스타*/ WHERE X.SO_ID = B.SO_ID ) AS SO_NM , A.CLC_WRK_NO , A.PROC_DTTM , A.CLC_PROC_STAT , (SELECT NVL(C.CODE_NM, B.COMMON_CD_NM) as COMMON_CD_NM FROM TSYCO_CODE_DETAIL B LEFT OUTER JOIN TSYCO_CODE_DETAIL_NAME C ON C.LNG_TYP = #{lng} AND C.COMMON_GRP = B.COMMON_GRP AND C.COMMON_CD = B.COMMON_CD WHERE B.COMMON_GRP = 'BLCH005' AND B.COMMON_CD = A.CLC_PROC_STAT) AS CLC_PROC_STAT_NM FROM TBLCH_CLC_MAIN A /* 요금게산메인 */ ,TBLCH_SVC_CMPS_MT B /* 서비스구성Master */ , TBLCH_CHRG C /* 요금내역 */ WHERE A.CLC_WRK_NO = B.CLC_WRK_NO AND B.CLC_WRK_NO = C.CLC_WRK_NO AND B.BILL_YYMM = C.BILL_YYMM AND B.BILL_CYCL = C.BILL_CYCL AND B.USE_YYMM = C.USE_YYMM AND B.SVC_CMPS_ID = C.SVC_CMPS_ID AND B.SO_ID = C.SO_ID AND A.CLC_WRK_CL = '5' /* 계산작업구분(5.계산) */ AND B.BILL_YYMM = #{condBillYymm} /* 청구년월 */ /* 계산처리상태 */ AND A.CLC_PROC_STAT = #{condClc}
/* 사업구분 */ AND A.SO_ID = #{soId}
/* 납부계정 */ AND B.PYM_ACNT_ID = #{condPymAcntId}
/* 고객 */ AND B.CUST_ID = #{condCustId}
GROUP BY A.CLC_WRK_NO, B.SO_ID, B.BILL_YYMM, B.BILL_CYCL, B.PYM_ACNT_ID, B.CUST_ID , B.CTRT_ID, B.PROD_CD, A.CLC_WRK_CL, A.PROC_DTTM, A.CLC_PROC_STAT ORDER BY PROC_DTTM DESC, A.CLC_WRK_NO DESC, SO_NM, B.CUST_ID, B.CTRT_ID, PROD_NM
) RESULT

/*com.ntels.ccbs.charge.mapper.charge.charge.ChargeCalculationMapper.getChargeDetailList 요금계산작업결과조회*/ SELECT RESULT.* FROM (
SELECT B.SO_ID , B.USE_YYMM , B.SVC_CD , C.RATE_ITM_CD , C.USE_AMT , C.USE_AMT , C.USE_QTY_UT , C.USE_QTY /* 이용건수 */ , C.USE_CNT /* 이용일수 */ -- , (SELECT SUM(USE_DD_CNT) -- FROM TBLCH_CHRG_CLC_DC_SPLT X /* 요금계산내역할인분리 */ -- WHERE X.CLC_WRK_NO = C.CLC_WRK_NO -- AND X.BILL_YYMM = C.BILL_YYMM -- AND X.BILL_CYCL = C.BILL_CYCL -- AND X.USE_YYMM = C.USE_YYMM -- AND X.SVC_CMPS_ID = C.SVC_CMPS_ID -- AND X.RATE_ITM_CD = C.RATE_ITM_CD -- ) AS USE_DD_CNT , (SELECT SVC_NM FROM TPMSV_SVC X /* 서비스정보 */ WHERE X.SVC_CD = B.SVC_CD AND X.MSTR_FL = '1' ) AS SVC_NM , (SELECT RATE_ITM_NM FROM TPMPD_RATE_ITM X /* 과금항목정보 TP_RATE_ITM -> VP_RATE_ITM 변경*/ WHERE X.RATE_ITM_CD = C.RATE_ITM_CD AND X.MSTR_FL = '1' ) AS RATE_ITM_NM /* 과금항목 */ FROM TBLCH_SVC_CMPS_MT B /* 서비스구성Master */ , TBLCH_CHRG C /* 요금내역 */ WHERE 1=1 AND B.CLC_WRK_NO = C.CLC_WRK_NO AND B.BILL_YYMM = C.BILL_YYMM AND B.BILL_CYCL = C.BILL_CYCL AND B.USE_YYMM = C.USE_YYMM AND B.SVC_CMPS_ID = C.SVC_CMPS_ID -- AND B.BILL_YYMM = #{condBillYymm} /* 납부계정 */ AND B.PYM_ACNT_ID = #{condPymAcntId}
/* 고객 */ AND B.CUST_ID = #{condCustId}
AND A.CLC_WRK_CL = '5' /* 계산작업구분(5.계산) */ /* 사업구분 */ AND B.SO_ID = #{soId}
ORDER BY SO_ID, SVC_NM, C.RATE_ITM_CD
) RESULT
