mapper.select:
SELECT A.SO_ID, (SELECT SO_NM FROM TSYCO_SO_MAST WHERE SO_ID = A.SO_ID) AS SO_NM, P1.PROD_CD, P1.PROD_NM, A.TIER_SET_CD, P3.RATE_ITM_NM, -- SUBSTR(A.ACT_DT,1,4)||'.'||SUBSTR(A.ACT_DT,5,2)||'.'||SUBSTR(A.ACT_DT,7,2) AS ACT_DT, CONCAT(SUBSTR(A.ACT_DT,1,4), CONCAT('.', CONCAT(SUBSTR(A.ACT_DT,5,2), CONCAT('.',SUBSTR(A.ACT_DT,7,2))))) AS ACT_DT, A.VER , A.PRICE_APPLY, ( SELECT C.CODE_NM AS COMMON_CD_NM FROM TSYCO_CODE_DETAIL B LEFT OUTER JOIN TSYCO_CODE_DETAIL_NAME C ON 'ko' = C.LNG_TYP AND B.COMMON_GRP = C.COMMON_GRP AND B.COMMON_CD = C.COMMON_CD WHERE B.COMMON_GRP = 'PP90011' AND B.COMMON_CD = A.PRICE_APPLY ) AS PRICE_APPLY_NM FROM TPMBI_TIER_SET A, TPMPD_RATE_ITM P3, TPMPD_PROD P1 WHERE 1=1
AND A.SO_ID = #{tierMng.searchSoId}
AND P1.PROD_CD = #{tierMng.searchProdCd}
AND P3.SO_ID = A.SO_ID AND P3.RATE_ITM_CD = A.TIER_SET_CD AND P3.ACT_DT <= #{tierMng.currentDay} AND P3.INACT_DT >= #{tierMng.currentDay} AND P1.SO_ID = P3.SO_ID AND P1.PROD_CD = P3.PROD_CD AND P1.ACT_DT <= #{tierMng.currentDay} AND P1.INACT_DT >= #{tierMng.currentDay}
ORDER BY A.SO_ID, A.TIER_SET_CD

SELECT COUNT(*)
	 FROM TPMBI_TIER_SET        A,
	      TPMPD_RATE_ITM        P3,
	      TPMPD_PROD            P1
	WHERE 1=1

       AND   A.SO_ID = #{tierMng.searchSoId}       


       AND  P1.PROD_CD = #{tierMng.searchProdCd}

	AND   P3.SO_ID         = A.SO_ID
	AND   P3.RATE_ITM_CD   = A.TIER_SET_CD
	AND   P3.ACT_DT   <= #{tierMng.currentDay}
  	AND   P3.INACT_DT >= #{tierMng.currentDay}   
	AND   P1.SO_ID         = P3.SO_ID
	AND   P1.PROD_CD       = P3.PROD_CD
    AND   P1.ACT_DT   <= #{tierMng.currentDay}
  	AND   P1.INACT_DT >= #{tierMng.currentDay}

SELECT TDP.PROD_CD , TDP.INACT_DT , TDP.ACT_DT , TDP.PROD_NM FROM TPMPD_PROD TDP WHERE 1=1 AND TDP.MSTR_FL = '1' AND TDP.ACT_DT <= #{tierMng.currentDay} AND TDP.INACT_DT >= #{tierMng.currentDay}
AND TDP.SO_ID = #{tierMng.searchSoIdPop}
AND TDP.PROD_NM LIKE CONCAT(CONCAT('%', #{tierMng.searchProdNmPop}), '%')
ORDER BY TDP.PROD_CD DESC

	SELECT COUNT(*)   
	FROM   TPMPD_PROD TDP
	WHERE  1=1
	  AND TDP.MSTR_FL = '1'
	  AND   TDP.ACT_DT   <= #{tierMng.currentDay}
	  AND   TDP.INACT_DT >= #{tierMng.currentDay}    

       AND   TDP.SO_ID = #{tierMng.searchSoId}       


       AND  TDP.PROD_NM LIKE CONCAT(CONCAT('%', #{tierMng.searchProdNm}), '%')
